sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below

	if(newValue != ''){
	g_form.setSectionDisplay('device_details',true);
	g_form.setSectionDisplay('delivery_details',true);
	}

   var glideAjax = new GlideAjax('getDeviceDetails');
   glideAjax.addParam('sysparm_name','getDetails');
   glideAjax.addParam('sysparm_device_id',newValue);
   glideAjax.getXMLAnswer(function(response){
      var deviceDetails = JSON.parse(response);

      g_form.setValue('u_model',deviceDetails.model);
      g_form.setValue('u_price',deviceDetails.price);
      g_form.setValue('u_device_description',deviceDetails.description);
      g_form.setValue('u_warranty_expiration',deviceDetails.warranty);


   });

}
