sys_script_include.script:
var getDeviceDetails = Class.create();
getDeviceDetails.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getDetails: function(){
		var deviceId = this.getParameter('sysparm_device_id');
		var deviceDetails = new GlideRecord('x_1097202_anny_app_ai_devices');
		deviceDetails.get(deviceId);
		deviceDetails.query();
		if(deviceDetails.next()){
			var outDeviceDetails = {
				model : deviceDetails.getValue('model_number'),
				price : deviceDetails.getValue('u_price_2'),
				description : deviceDetails.getValue('short_description'),
				warranty : deviceDetails.getValue('warranty_expiration')
			}

			return JSON.stringify(outDeviceDetails);
		}
	},

    type: 'getDeviceDetails'
});
